// Parse and format AI response
const input = $input.first().json;
const taskType = $('Webhook').first().json.task_type;

let result;
try {
  let content = input.message?.content || input.choices?.[0]?.message?.content || input.text || input.content || JSON.stringify(input);

  if (typeof content === 'string') {
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      result = JSON.parse(jsonMatch[0]);
    } else {
      result = { rawResponse: content };
    }
  } else {
    result = content;
  }
} catch (error) {
  result = { 
    error: 'Failed to parse AI response',
    rawResponse: input,
    parseError: error.message
  };
}

return [{
  json: {
    success: !result.error,
    task: taskType,
    timestamp: new Date().toISOString(),
    result: result,
    metadata: {
      model: 'gpt-4o',
      version: '2.0',
      processedAt: Date.now()
    }
  }
}];
